import $ from 'jquery'

// function for popover show hide 
$(function() {
	function OpenPopover( event ) {
	  var target = $( event.target );
	  var checkelement = $(this).data("toggle");
	  $('#' + checkelement).toggle();
	   event.stopPropagation();
	}
	$( ".filterpill" ).click( OpenPopover );
});
