import $ from 'jquery'

$(function() {
	$( ".filterpill" ).click( OpenPopover ); // open popovers
});

// function for popover show hide 
function OpenPopover( event ) {
  var target = $( event.target );
  var checkelement = $(this).data("target");
  $('#' + checkelement).fadeToggle();
}
