import $ from 'jquery'

$(function() {

//$('.filterpill').on('click.popover', function() { alert('test'); });





function OpenPopover( event ) {
  var target = $( event.target );
  $(this).data("toggle");
  if ( target.is( "button" ) ) {
    //target.children().toggle();
    //$(".popover").toggle()
    alert($(this).html());
  }
}
$( ".filterpill" ).click( OpenPopover );



});
