import $ from 'jquery'

$(function() {

//$('.filterpill').on('click.popover', function() { alert('test'); });





function OpenPopover( event ) {
  var target = $( event.target );
  var checkelement = $(this).data("toggle");
  if ( checkelement == "popover" ) {
    //$('#'.checkelement).toggle();
    alert('#'.checkelement);
  }
}
$( ".filterpill" ).click( OpenPopover );



});
