import React, {Component} from "react";
import TemplateDefault from '../../components/TemplateDefault';
import TemplateComponentBlock from '../../components/TemplateComponentBlock';

export default class Messages extends Component {

  constructor(props) {
    super(props);

    this.elements = [
      `<div class="row justify-content-center">
        <div class="col col-12 pt-3">          
          <div class="message message--primary">This is a primary alert</div>
          <div class="component_content__demo__description">primary alert</div> 
        </div>
        <div class="col col-12 pt-3">          
          <div class="message message--secondary">This is a secondary alert</div>
          <div class="component_content__demo__description">secondary alert</div> 
        </div>
       </div>
       `,
    ];


    this.codeSnippets = [
``,			
];

  }

  render(){
    return (
      <TemplateDefault
        pageTitle="Messages"
        activeParent="components"
        activeChild="messages">

        <TemplateComponentBlock blockTitle="Listing Labels" elements={this.elements[0]} codeSnippets={this.codeSnippets[0]} />        
      </TemplateDefault>
    )
  }

}
